            if(attributes[i].key.substr(0,2)=="__"){
                if (attributes[i].key == "__alarma_" + vm
					.subtipo) {
					var alarmas = JSON.parse(attributes[i]
						.value);
					for (var clave in alarmas) {
						vm.configuracion.__alarmas[clave] =
							alarmas[clave];
					}
				}else{
				    vm.configuracion[attributes[i].key] =
