                    attributes[i].value;
            }
        }
        
        vm.serverAttributes = angular.copy(vm.attributes);
    }

    function getTiposDeActivosYDispositivos(attributes) {
        for (var i = 0; i < attributes.length; i++) {
            if (attributes[i].key ==
                "tiposDeDispositivos") {
                vm.tiposDeDispositivos = attributes[i].value
                    .split(",");
            } else if (attributes[i].key ==
                "tiposDeActivos") {
                vm.tiposDeActivos = attributes[i].value
                    .split(",");
            } else if (attributes[i].key ==
                "email") {
                if (attributes[i].value !== "") {
                    vm.viasNotificacion.email = true;
                } else {
                    vm.viasNotificacion.email = false;
                }
            } else if (attributes[i].key ==
                "token_telegram") {
                if (attributes[i].value !== "") {
                    vm.viasNotificacion.telegram = true;
                } else {
                    vm.viasNotificacion.telegram = false;
                }

            } else if (attributes[i].key ==
                "token_webhook_ifttt") {
                if (attributes[i].value !== "") {
                    vm.viasNotificacion.ifttt = true;
                } else {
                    vm.viasNotificacion.ifttt = false;
                }
            }
        }

    }


    function getActivos(activos) {
        //Los uso para establecer los padres
