        for (var i = 0; i < activos.length; i++) {
            //Si es un activo no debe incluirse a sí mismo
            if (vm.entityType == "DEVICE" || vm.entityId
                .id !=
                activos[i].id.id) {
                getAtributosActivo(activos[i].id.id,
