            } else {
                //console.log("b_" + vm.entityId.id + "__" +activos[i].id.id);
            }
        }
        //console.log(vm.activos);
    }

    function getAtributosActivo(id, nombreActivo,
        tipoActivo) {
        attributeService.getEntityAttributesValues('ASSET',
            id, 'SERVER_SCOPE').then(
            function success(data) {
                var parametros = {};
                for (var i = 0; i < data.length; i++) {
                    //console.log(data[i].key,data[i].value);
					if(data[i].key=="nombreEntidad"){
					parametros['nombreEntidad'] = data[i]
                        .value.substr(1);
					}else{
                    parametros[data[i].key] = data[i]
                        .value;
						}
                }
                vm.activos[nombreActivo] = {
                    'tipo': tipoActivo,
                    'atributos': parametros
                };
                //console.log(vm.activos[nombreActivo]);

            });
    }


    function getEntityRelations(relations) {
        var relationsFrom = relations[0];
        var relationsTo = relations[1];
        //Si tiene hijos no se puede borrar
        vm.borrable = true;
        for (var i = 0; i < relationsFrom.length; i++) {
            vm.borrable = false;
            var relation = {
                direction: types
                    .entitySearchDirection.from,
                relationType: relationsFrom[i].type,
                relatedEntity: relationsFrom[i].to
            };
            vm.relations.push(relation);
        }

        for (var i = 0; i < relationsTo.length; i++) {

            var relation = {
                direction: types
                    .entitySearchDirection.to,
                relationType: relationsTo[i].type,
                relatedEntity: relationsTo[i].from
            };
            if (relation.relationType == "Contains" &&
                relation.relatedEntity.entityType == "ASSET"
            ) {
                establecerNombrePadre(relation.relatedEntity
                    .id);
                //Necesito el nombre del padre
            }
            vm.relations.push(relation);
        }

    }

    function establecerNombrePadre(id) {
        assetService.getAsset(id).then(
            function(data) {
                vm.padre = data.name;
                //console.log(vm.padre);
            }
        );
    }

    function getEntityInfo() {
        entityService.getEntity(entityId.entityType,
            entityId.id).then(
            function(entity) {
                vm.entity = entity;
                vm.type = vm.entity.type;
            });

        attributeService.getEntityAttributesValues(
            entityId.entityType, entityId.id,
            'SERVER_SCOPE').then(
            function(data) {
                if (data.length) {
                    getEntityAttributes(data);
                }
            });
        //Obtngo los tipos de activos y dispositivos 
        //que tiene permitidos el customer en sus atributos
        //y también las vías de notificación (ifttt, telegram...)
        attributeService.getEntityAttributesValues(
            'CUSTOMER', userService.getCurrentUser()
            .customerId,
            'SERVER_SCOPE').then(
            function(data) {
                if (data.length) {
                    getTiposDeActivosYDispositivos(
                        data);
                }
            });
        //Obtengo los activos del customer para poder
        //mostrarlos como potenciales padres
        assetService.getCustomerAssets(userService
            .getCurrentUser()
            .customerId, {
                "limit": 100
            }).then(
            function(data) {
                if (data.data.length) {
                    getActivos(data.data);

                }
            });

        //Obtengo el token del dispositivo SYSTEM
        deviceService.getCustomerDevices(userService
            .getCurrentUser().customerId, {
                "limit": 100
            }, false, {}, 'SYSTEM').then(
            function(data) {
                if (data.data.length == 1) {
                    deviceService.getDeviceCredentials(
                        data.data[0].id.id, true, {}
                    ).then(
                        function(response) {
                            vm.tokenControl =
                                response
                                .credentialsId;

                        });
                }
            });


        $q.all([entityRelationService.findInfoByFrom(
                entityId.id, entityId.entityType
            ), entityRelationService
            .findInfoByTo(entityId.id, entityId
                .entityType)
        ]).then(
            function(relations) {
                getEntityRelations(relations);
            });

    }

    function saveAttributes() {
        var attributesArray = [];
        for (var key in vm.attributes) {
            if (vm.attributes[key] !== vm
                .serverAttributes[key]) {
                attributesArray.push({
                    key: key,
                    value: vm.attributes[key]
                });
            }
        }
        if (attributesArray.length > 0) {
            attributeService.saveEntityAttributes(
                entityId.entityType, entityId.id,
                "SERVER_SCOPE", attributesArray);
        }
    }

    function saveRelations() {
        var tasks = [];
        for (var i = 0; i < vm.newRelations
            .length; i++) {
            var relation = {
                type: vm.newRelations[i]
                    .relationType
            };
            if (vm.newRelations[i].direction == types
                .entitySearchDirection.from) {
                relation.to = vm.newRelations[i]
                    .relatedEntity;
                relation.from = entityId;
            } else {
                relation.to = entityId;
                relation.from = vm.newRelations[i]
                    .relatedEntity;
            }
            tasks.push(entityRelationService
                .saveRelation(relation));
        }
        for (var i = 0; i < vm.relationsToDelete
            .length; i++) {
            var relation = {
                type: vm.relationsToDelete[i]
                    .relationType
            };
            if (vm.relationsToDelete[i].direction ==
                types.entitySearchDirection.from) {
                relation.to = vm.relationsToDelete[i]
                    .relatedEntity;
                relation.from = entityId;
            } else {
                relation.to = entityId;
                relation.from = vm.relationsToDelete[i]
                    .relatedEntity;
            }
            tasks.push(entityRelationService
                .deleteRelation(relation.from.id,
                    relation.from.entityType,
                    relation.type, relation.to.id,
                    relation.to.entityType));
        }
        $q.all(tasks).then(function() {
            vm.relations = vm.relations.concat(
                vm.newRelations);
            vm.newRelations = [];
            vm.relationsToDelete = [];
            updateAliasData();
        });
    }

    function updateAliasData() {
        var aliasIds = [];
        for (var id in widgetContext.aliasController
                .resolvedAliases) {
            aliasIds.push(id);
        }
        var tasks = [];
        aliasIds.forEach(function(aliasId) {
            widgetContext.aliasController
                .setAliasUnresolved(aliasId);
            tasks.push(widgetContext
                .aliasController
                .getAliasInfo(aliasId));
        });
        $q.all(tasks).then(function() {
            $rootScope.$broadcast(
                'entityAliasesChanged',
                aliasIds);
        });
    }
}
