    .delegadoActual.length > 0) {
    //Es un delegadoActual
    var delegadoActual = JSON.parse(metadata
        .delegadoActual).delegaciones;
    for (var telemetria in msg) {
        if (delegadoActual.hasOwnProperty(telemetria) &&
            delegadoActual[telemetria] === true) {
            newMsg[telemetria] = msg[telemetria];
        } else {
            //Si no tiene la delegación o es false
            if (!metadata.hasOwnProperty(telemetria) ||
                metadata[telemetria] != "No delegado") {
                newMsg[telemetria] = "No delegado";
            }
        }
    }

} else {
    newMsg = msg;
}
return {
    msg: newMsg,
    metadata: metadata,
    msgType: msgType
};

return {
