//0188069e16ff1f5500641e0802010c0371fedc000103c00586ff710074ffa60902fbf50a0208bb0b02e197
var comodin_payload_raw = newMsg.payload_raw;
//Creo el decodificador LPP
var payload_fields = {};
while (comodin_payload_raw.length > 4) {
    switch (comodin_payload_raw.substring(2, 4)) {
        case "00":
            payload_fields['digital_in_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 6),
                    16);
            comodin_payload_raw = comodin_payload_raw
                .substring(6);
            break;
        case "01":
            payload_fields['digital_out_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 6),
                    16);
            comodin_payload_raw = comodin_payload_raw
                .substring(6);
            break;
        case "02":


                    valor = parseInt(
                comodin_payload_raw.substring(4,
                    8),
                16);

            if ((valor & 0x8000) > 0) {
                payload_fields['analog_in_' +parseInt(comodin_payload_raw.substring(0, 2), 16)] =((valor & 0x7FFF ) - 0x8000)/ 100.0 ;
            } else {
                payload_fields['analog_in_' +
                    parseInt(comodin_payload_raw.substring(0, 2), 16)] =valor/ 100.0 ;

            }



            comodin_payload_raw = comodin_payload_raw
                .substring(8);
            break;
        case "03":
            payload_fields['analog_out_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 8),
                    16) / 100.0;
            comodin_payload_raw = comodin_payload_raw
                .substring(8);
            break;
        case "65":
            payload_fields['luminosity_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 8),
                    16);
            comodin_payload_raw = comodin_payload_raw
                .substring(8);
            break;
        case "66":
            payload_fields['presence_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 6),
                    16);
            comodin_payload_raw = comodin_payload_raw
                .substring(6);
            break;
        case "67":
            payload_fields['temperature_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 8),
                    16) / 10.0;
            comodin_payload_raw = comodin_payload_raw
                .substring(8);
            break;
        case "68":
            payload_fields['relative_humidity_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 6),
                    16) / 2.0;
            comodin_payload_raw = comodin_payload_raw
                .substring(6);
            break;
        case "73":
            payload_fields[
                    'barometric_pressure_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 8),
                    16) / 10.0;
            comodin_payload_raw = comodin_payload_raw
                .substring(8);
            break;
        case "71":
            var valor;
            var campo = 'accelerometer_' +
                parseInt(comodin_payload_raw
                    .substring(0, 2), 16);

            payload_fields[campo] = {};

            valor = parseInt(
                comodin_payload_raw.substring(4,
                    8),
                16);

            if ((valor & 0x8000) > 0) {
                payload_fields[campo]
                    .x =
                    ((valor & 0x7FFF ) - 0x8000)/ 1000.0 ;
            } else {
                payload_fields[campo]
                    .x =
                    valor/ 1000.0 ;

            }




            valor = parseInt(
                comodin_payload_raw.substring(8,
                    12),
                16);


            if ((valor & 0x8000) > 0) {
                payload_fields[campo]
                    .y =
                    ((valor& 0x7FFF ) - 0x8000) / 1000.0;
            } else {
                payload_fields[campo]
                    .y =
                    valor / 1000.0;

            }



            valor = parseInt(
                comodin_payload_raw.substring(12,
                    16),
                16);


            if ((valor & 0x8000) > 0) {
                payload_fields[campo]
                    .z =
                    ((valor& 0x7FFF ) - 0x8000) / 1000.0;
            } else {
                payload_fields[campo]
                    .z =
                    valor / 1000.0;

            }


            comodin_payload_raw = comodin_payload_raw
                .substring(16);
            break;
        case "86":
            var valor;
            var campo = 'gyrometer_' +
                parseInt(comodin_payload_raw
                    .substring(0, 2), 16);

            payload_fields[campo] = {};
            valor = parseInt(
                comodin_payload_raw.substring(4,
                    8),
                16);


            if ((valor & 0x8000) > 0) {
                payload_fields[campo]
                    .x =
                    ((valor& 0x7FFF ) - 0x8000) / 100.0;

            } else {
                payload_fields[campo]
                    .x =
                    valor / 100.0;
            }


            valor = parseInt(
                comodin_payload_raw.substring(8,
                    12),
                16);


           if ((valor & 0x8000) > 0) {
                payload_fields[campo]
                    .y =
                    ((valor& 0x7FFF ) - 0x8000) / 100.0;
            } else {
                payload_fields[campo]
                    .y =
                    valor / 100.0;

            }



            valor = parseInt(
                comodin_payload_raw.substring(12,
                    16),
                16);


           if ((valor & 0x8000) > 0) {
                payload_fields[campo]
                    .z =
                    ((valor& 0x7FFF ) - 0x8000) / 100.0;
            } else {
                payload_fields[campo]
                    .z =
                    valor / 100.0;

            }




            comodin_payload_raw = comodin_payload_raw
                .substring(16);
            break;
        case "88":
            var valor;
            var campo = 'gps_' +
                parseInt(comodin_payload_raw
                    .substring(0, 2), 16);

            payload_fields[campo] = {};
            valor = parseInt(
                comodin_payload_raw.substring(4,
                    10),
                16);

            if ((valor & 0x800000) > 0) {
                payload_fields[campo]
                    .latitude = (
                        valor - 0x1000000) / 10000.0;

            } else {
                payload_fields[campo]
                    .latitude =
                    valor / 10000.0;
            }

            valor = parseInt(
                comodin_payload_raw.substring(10,
