                16);
            if ((valor & 0x800000) > 0) {
                payload_fields[campo]
                    .longitude = (
                        valor - 0x1000000) / 100.0;
            } else {
                payload_fields[campo]
                    .altitude =
                    valor / 100.0;


            }
            comodin_payload_raw = comodin_payload_raw
                .substring(22);
            break;
    }
}



