            if ((valor & 0x8000) > 0) {
                payload_fields[campo]
                    .x =
                    ((valor & 0x7FFF) - 0x8000) / 100.0;

            } else {
                payload_fields[campo]
                    .x =
                    valor / 100.0;
            }


            valor = parseInt(
                comodin_payload_raw.substring(8,
                    12),
                16);


            if ((valor & 0x8000) > 0) {
                payload_fields[campo]
                    .y =
                    ((valor & 0x7FFF) - 0x8000) / 100.0;
            } else {
                payload_fields[campo]
                    .y =
                    valor / 100.0;

            }



            valor = parseInt(
                comodin_payload_raw.substring(12,
                    16),
                16);


            if ((valor & 0x8000) > 0) {
                payload_fields[campo]
                    .z =
                    ((valor & 0x7FFF) - 0x8000) / 100.0;
            } else {
                payload_fields[campo]
                    .z =
