if (payload_fields.hasOwnProperty('analog_in_4') &&
    payload_fields.hasOwnProperty('temperature_2') &&
    payload_fields.hasOwnProperty('relative_humidity_7')) {
    //Uso esta referencia para calcular el IAQ
    //https://github.com/G6EJD/BME680-Example
    //David Bird 2018
    //Asumo rango de resistencia de 0 a 50000
    //Doy un peso del 10% a la humedad
    //y otro 10% a la temperatura

    var resistencia = 100.0 - (Math.min(50000, Math.abs(
            payload_fields.analog_in_4*1000.0)) * 100.0 /
        50000.0);
    var temperatura;
    if (payload_fields.temperature_2 < 21) {
        temperatura = (payload_fields.temperature_2 -
            21.0) * 100.0 / (-41.0);
    } else {
        temperatura = (payload_fields.temperature_2 -
            21.0) * 100.0 / 79.0;
    }
    var humedad;
    if (payload_fields.relative_humidity_7 < 40) {
        humedad = (payload_fields.relative_humidity_7 -
            40.0) * 100.0 / (-40.0);
    } else {
        humedad = (payload_fields.relative_humidity_7 -
            40.0) * 100.0 / 60.0;
    }
    newMsg.iaq = 0.8 * resistencia + 0.1 * temperatura +
        0.1 * humedad; //en porcentaje
    newMsg.iaq = 5.0 * newMsg.iaq;

}

