configuration.jsScript:
 /*
 Tengo que pasar de formato TTN:
 {
    "port": 99,
    "confirmed": true,
    "payload_raw": "BgBk/w==",
    "dev_id": "nodo1"
}
a formato CS:
 {  "deviceQueueItem": { 
     "confirmed": true,  
     "data": "AQI=",  
     "devEUI": "009494db3143aea3",
     "fCnt": 0,
     "fPort": 99  
   } 
 }
 */
 var newMsg = {};
 newMsg.deviceQueueItem = {
     "confirmed": true,
     "data": msg.payload_raw,
     "devEUI": metadata.ss_hardware_serial,
     "fPort": 99
 };

metadata.deviceQueueItem=JSON.stringify(newMsg);
 return {
     msg: msg,
     metadata: metadata,
     msgType: msgType
 };

var respuesta=[];
if (metadata.hasOwnProperty('ss_downlink_url') &&
    metadata.hasOwnProperty('ss_ttn_dev_id')) {
    respuesta.push('TTN');
}
if (metadata.hasOwnProperty('ss_downlink_chirpstack') &&
    metadata.ss_downlink_chirpstack === "true" &&
    metadata.hasOwnProperty('ss___cs_token') &&
    metadata.hasOwnProperty('ss___cs_url')&&
    metadata.hasOwnProperty('ss_hardware_serial')) {
    respuesta.push('CS');
}

return respuesta;

var newMsg = {};
newMsg.app_id = msg.applicationName;
newMsg.dev_id = msg.deviceName;
newMsg.hardware_serial = msg.devEUI;
newMsg.adr = msg.adr;
newMsg.counter = msg.fCnt;
newMsg.port = msg.fPort;
newMsg.payload_raw = msg.data;
var newPayloadFields = msg.object;
//Desafortunadamente el Cayenne Codec de CS es muy diferente al de TTN
//No obstante, lo utilizaré porque aquí se hace una vez, pero si hubiera que
//hacerlo en CS implicaría actuar sobre cada aplicación
//"object": "{\"analogInput\":{\"1\":2.74},\"digitalInput\":{\"2\":1,\"3\":0}}"
//"payload_fields": "{\"analog_in_1\":2.75,\"digital_in_2\":1,\"digital_in_3\":0}"
newMsg.payload_fields = {};
for (var key in newPayloadFields) {
    var prefijo = "";
    switch (key) {
        case 'analogInput':
            prefijo = 'analog_in_';
            break;
        case 'digitalInput':
            prefijo = 'digital_in_';
            break;
        case 'temperatureSensor':
            prefijo = 'temperature_';
            break;
        case 'humiditySensor':
            prefijo = 'relative_humidity_';
            break;
        case 'accelerometer':
            prefijo = 'accelerometer_';
            break;
        case 'barometer':
            prefijo = 'barometric_pressure_';
            break;
        case 'gyrometer':
            prefijo = 'gyrometer_';
            break;
        case 'gpsLocation':
            prefijo = 'gps_';
            break;
        case 'digitalOutput':
            prefijo = 'digital_out_';
            break;
        case 'analogOutput':
            prefijo = 'analog_out_';
            break;
        case 'illuminanceSensor':
            prefijo = 'luminosity_';
            break;
        case 'presenceSensor':
            prefijo = 'presence_';
            break;

    }
    for (var key2 in newPayloadFields[key]) {
        newMsg.payload_fields[prefijo + key2] =
            newPayloadFields[key][key2];
    }
}
newMsg.payload_fields = newMsg
    .payload_fields;
newMsg.metadata = {
    'rxInfo': msg.rxInfo,
    'txInfo': msg.txInfo
};
newMsg.metadata = newMsg.metadata;
newMsg.downlink_url = "chirpstack";
return {
    msg: newMsg,
    metadata: metadata,
    msgType: msgType
};

if (metadata.hasOwnProperty('tiposDeDispositivos') &&
    metadata.hasOwnProperty('deviceType')) {
    return metadata.tiposDeDispositivos.split(",").indexOf(
        metadata.deviceType) >= 0;
} else {
    return false;
}

var newObj = msg.payload_fields;
var obj = {};
//Si el obj tiene propiedades que son, a su vez, objetos JSON
//por ejemplo, el caso de un GPS
//tengo que convertirlas a timeseries
//Desafortunadamente, es posible que aquí tengamos que hacer algunas
//conversiones porque ciertos widgets no admiten los datos en el formato
//que los entregan los sensores; por ejemplo, el magnetómetro del RAK7200
//entrega las mediciones en uT, pero el widget brújula requiere directamente un ángulo
//Esto lo podemos resolver con una función de postprocesamiento en el propio alias
//pero el Widget trip animation sólo admite timeseries (no attibutes), por lo que no
//podemos cargar el cercado desde un atributo (o quizás sea mejor tratarlo en la regla del propio dispositivo)
for (var x in newObj) {
    if (typeof(newObj[x]) === 'object') {
        for (var y in newObj[x]) {
            obj[x + '_' + y] = newObj[x][y];
        }
    } else {
        obj[x] = newObj[x];
    }
}
obj.metadata = msg.metadata;
obj.downlink_url = msg.downlink_url;
obj.ttn_dev_id = msg.dev_id;
obj.hardware_serial = msg.hardware_serial;
return {
    msg: obj,
    metadata: metadata,
    msgType: msgType
};

if (metadata.deviceType == "SYSTEM") {
    if (msg.hasOwnProperty('accion')) {
        if (msg.accion == 'editarPerfil') {
            return ['editarPerfil'];
        } else if (msg
            .accion == 'tomarPosesion') {
            if (msg
                .hasOwnProperty('uuid') && msg
                .hasOwnProperty(
                    'payload')) {
                return ['tomarPosesion'];
            }
        } else if (msg
            .accion == 'enviarDownlink') {
            if (msg
                .hasOwnProperty('uuid') && msg
                .hasOwnProperty(
                    'payload')) {
                return ['enviarDownlink'];
            }
        } else if (msg
            .accion == 'reclamarDispositivo') {
            if (msg.hasOwnProperty('idEntidad') && msg
                .hasOwnProperty('nombreEntidad') && msg
                .hasOwnProperty('subtipoEntidad') ) {
                return ['reclamarDispositivo'];
            }
        } else if (msg
            .accion == 'editarEntidad') {
            if (msg.hasOwnProperty('idEntidad') && msg
                .hasOwnProperty('nombreEntidad') && msg
                .hasOwnProperty('tipoEntidad') && msg
                .hasOwnProperty('subtipoEntidad') && msg
                .hasOwnProperty('padreEntidad') && msg
                .hasOwnProperty('tipoPadreEntidad')) {
                return ['editarEntidad'];
            }
        } else if (msg
            .accion == 'configurarEntidad') {
            if (msg.hasOwnProperty('configuracion') ) {
                return ['configurarEntidad'];
            }
        } else if (msg.accion == 'crearEntidad') {
            if (msg.hasOwnProperty('nombreEntidad') && msg
                .hasOwnProperty('tipoEntidad') && msg
                .hasOwnProperty('subtipoEntidad') && msg
                .hasOwnProperty('padreEntidadId') && msg
                .hasOwnProperty('padreEntidadNombre')) {
                return ['crearEntidad'];
            }
        } else if (msg.accion == 'borrarEntidad') {
            if (msg.hasOwnProperty('nombreEntidad') && msg
                .hasOwnProperty('tipoEntidad')) {
                return ['borrarEntidad'];
            }
        }
    }
} else {
    return false;
}

msgType = 'POST_ATTRIBUTES_REQUEST';
//Valido los atributos que admito modificar
var mensaje={};
if(msg.hasOwnProperty('email')){
    mensaje.email=msg.email;
}
if(msg.hasOwnProperty('token_telegram')){
    mensaje.token_telegram=msg.token_telegram;
}
if(msg.hasOwnProperty('chatid_telegram')){
    mensaje.chatid_telegram=msg.chatid_telegram;
}
if(msg.hasOwnProperty('token_webhook_ifttt')){
    mensaje.token_webhook_ifttt=msg.token_webhook_ifttt;
}

return {msg: mensaje, metadata: metadata, msgType: msgType};

//Esta información me va a servir para enviar downlinks con el formato de TTN, que requiere el URL y el device_id
//Si el url es chirpstack, ya lo trataré en el código

var newMessage = {};
if (msg.hasOwnProperty('ttn_dev_id')) {
    newMessage.ttn_dev_id = msg.ttn_dev_id;
}

if (msg.hasOwnProperty('downlink_url')&& msg.downlink_url!='chirpstack') {
    newMessage.downlink_url = msg.downlink_url;
}

if (msg.hasOwnProperty('downlink_url')&& msg.downlink_url=='chirpstack') {
    newMessage.downlink_chirpstack = true;
}
newMessage.hardware_serial=msg.hardware_serial;
return {
    msg: newMessage,
    metadata: metadata,
    msgType: 'POST_ATTRIBUTES_REQUEST'
};

msgType = 'POST_ATTRIBUTES_REQUEST';
var mensaje = {};
if(msg.payload_raw.substring(0,6)=="RkYwM0"){
    //Sólo cambio si se llega a enviar el tercer downlink
    mensaje.apropiado=true;
    return {
    msg: mensaje,
    metadata: metadata,
    msgType: msgType
};
}else{
    return null;
}

if (msg.hasOwnProperty('payload_fields') &&
    msg.hasOwnProperty('metadata') &&
    msg.hasOwnProperty('dev_id')) {
    return ['TTN'];
}else if (msg.hasOwnProperty('data') &&
    msg.hasOwnProperty('object') &&
    msg.hasOwnProperty('rxInfo') &&
    msg.hasOwnProperty('txInfo') &&
    msg.hasOwnProperty('deviceName')) {
    return ['CS'];

} else {
    return ['No_TTN_CS']
}

msg=JSON.parse(metadata.deviceQueueItem);
return {
    msg: msg,
    metadata: metadata,
    msgType: msgType
};
