var comodin_payload_raw = newMsg.payload_raw;
//Creo el decodificador LPP
var payload_fields = {};
while (comodin_payload_raw.length > 4) {
    switch (comodin_payload_raw.substring(2, 4)) {
        case "00":
            payload_fields['digital_in_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 6),
                    16);
            comodin_payload_raw = comodin_payload_raw
                .substring(6);
            break;
        case "01":
            payload_fields['digital_out_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 6),
                    16);
            comodin_payload_raw = comodin_payload_raw
                .substring(6);
            break;
        case "02":


            valor = parseInt(
                comodin_payload_raw.substring(4,
                    8),
                16);

            if ((valor & 0x8000) > 0) {
                payload_fields['analog_in_' + parseInt(
                    comodin_payload_raw.substring(0,
                        2), 16)] = ((valor & 0x7FFF) -
                    0x8000) / 100.0;
            } else {
                payload_fields['analog_in_' +
                        parseInt(comodin_payload_raw
                            .substring(0, 2), 16)] = valor /
                    100.0;

            }



            comodin_payload_raw = comodin_payload_raw
                .substring(8);
            break;
        case "03":
            payload_fields['analog_out_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 8),
                    16) / 100.0;
            comodin_payload_raw = comodin_payload_raw
                .substring(8);
            break;
        case "65":
            payload_fields['luminosity_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 8),
                    16);
            comodin_payload_raw = comodin_payload_raw
                .substring(8);
            break;
        case "66":
            payload_fields['presence_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
                parseInt(
                    comodin_payload_raw.substring(4, 6),
                    16);
            comodin_payload_raw = comodin_payload_raw
                .substring(6);
            break;
        case "67":
            payload_fields['temperature_' +
                    parseInt(comodin_payload_raw
                        .substring(0, 2), 16)] =
