configuration.jsScript:
var respuesta = [];
if (metadata.hasOwnProperty('ss_alarma_V02_001')) {
    var obj = JSON.parse(metadata.ss_alarma_V02_001);
    if (obj.hasOwnProperty('cambioDeEstado')&&msg.hasOwnProperty('digital_in_2')) {
        respuesta.push('cambioDeEstado');
    }
    if (obj.hasOwnProperty('nivelDeBateria')&&msg.hasOwnProperty('analog_in_1')) {
        respuesta.push('nivelDeBateria');
    }
    if (obj.hasOwnProperty('inactividad')&&(msgType=='INACTIVITY_EVENT'||msgType=='ACTIVITY_EVENT')) {

        respuesta.push('inactividad');
    }
}

return respuesta;

var obj = JSON.parse(metadata.ss_alarma_V02_001);

if (obj.cambioDeEstado.enable===true){
    msg.alarma=obj.cambioDeEstado;
    //Cargo los datos de interés en los metadatos para construir los mensaje de alarma
    metadata.analog_in_1=msg.analog_in_1;
    metadata.digital_in_2=msg.digital_in_2;
    metadata.alarmaActual=JSON.stringify(obj.cambioDeEstado);

   return {
        msg: msg,
        metadata: metadata,
        msgType: msgType
    };
}

