let $scope;
self.onInit = function() {
    $scope = self.ctx.$scope;
    userService = $scope.$injector.get('userService');
    deviceService = $scope.$injector.get('deviceService');
    servicioHTTP = $scope.$injector.get('$http');
     $location = $scope.$injector.get('$location');
    attributeService = $scope.$injector.get(
        'attributeService');
    if (userService.getCurrentUser().authority ==
        "CUSTOMER_USER") {
        attributeService.getEntityAttributesValues(
            'CUSTOMER', userService.getCurrentUser()
            .customerId,
            'SERVER_SCOPE').then(
            function(data) {
                if (data.length) {
                    getAtributos(
                        data);
                }

            });
        //Obtengo el token del dispositivo SYSTEM
        deviceService.getCustomerDevices(userService
            .getCurrentUser().customerId, {
                "limit": 100
            }, false, {}, 'SYSTEM').then(
            function(data) {
                if (data.data.length == 1) {
                    deviceService
                        .getDeviceCredentials(
                            data.data[0].id.id,
                            true, {}
                        ).then(
                            function(response) {
                                $scope
                                    .tokenControl =
                                    response
