        if(accion=="borrar"){
            $scope.email="";
        }
        servicioHTTP({
            method: 'POST',
            url: $location.protocol() + '://' +
                $location.host() +
                '/api/v1/' + $scope.tokenControl +
                '/telemetry',
            data: "{'accion':'editarPerfil','email':'" + $scope.email + "'}"
        }).
        success(function(data, status, headers,
            config) {
            console.log('worked');

