self.onInit = function() {
    var scope = self.ctx.$scope;

    userService = scope.$injector.get('userService');
    deviceService = scope.$injector.get(
        'deviceService');
    servicioHTTP = scope.$injector.get('$http');
    $location = scope.$injector.get('$location');
    scope.datasources = self.ctx.defaultSubscription
        .datasources;
    scope.data = self.ctx.defaultSubscription.data;
    if (userService.getCurrentUser().authority ==
        "CUSTOMER_USER") {
        //Obtengo el token del dispositivo SYSTEM
        deviceService.getCustomerDevices(userService
            .getCurrentUser().customerId, {
                "limit": 100
            }, false, {}, 'SYSTEM').then(
            function(data) {
                if (data.data.length == 1) {
                    deviceService
                        .getDeviceCredentials(
                            data.data[0].id.id,
                            true, {}
                        ).then(
                            function(response) {
                                scope
                                    .tokenControl =
                                    response
                                    .credentialsId;

                            });
                }
            });
    }
    scope.enviarDownlink = function() {
        /*
        {
  "dev_id": "my-dev-id",    // The device ID
  "port": 1,                // LoRaWAN FPort
  "confirmed": false,       // Whether the downlink should be confirmed by the device
  "payload_raw": "AQIDBA==" // Base64 encoded payload: [0x01, 0x02, 0x03, 0x04]
}

*/
        var payload = {};
        payload.port =
        99; //Cayenne mydevices manda los downlink por el port 99
        payload.confirmed = true;
        payload.payload_raw = String.fromCharCode(
            parseInt(self.ctx
                .settings.outputChannel));
        payload.payload_raw += String.fromCharCode(
            0);

        if (scope.isActive === true) {
            payload.payload_raw += String
                .fromCharCode(100);
        } else {
            payload.payload_raw += String
                .fromCharCode(0);
        }
        payload.payload_raw += String.fromCharCode(
            255);
        payload.payload_raw = btoa(payload
            .payload_raw);
        servicioHTTP({
            method: 'POST',
            url: $location.protocol() +
                '://' +
                $location.host() +
                '/api/v1/' + scope
                .tokenControl +
                '/telemetry',
            data: "{'accion':'enviarDownlink','uuid':'" +
                self.ctx.$scope.data[0]
                .datasource.entity.id.id +
                "','payload':'" +
                JSON.stringify(payload) +
                "'}"
        }).
        success(function(data, status, headers,
            config) {
            console.log('worked');

            // ...
        }).
        error(function(data, status, headers,
            config) {
            console.log('notworked');
            // ...
        });
    }
}
self.onDataUpdated = function() {
    var scope = self.ctx.$scope;
    //console.log(scope.data[0].data[0][1]);
    if (scope.data[0].data[0][1] == 1) {
        scope.estadoActual = "true";
        document.getElementById("estadoActual")
            .innerHTML = "Activado";
        scope.isActive = true;
    } else {
        scope.estadoActual = "false";
        document.getElementById("estadoActual")
            .innerHTML = "Desactivado";
        scope.isActive = false;
    }
    scope.$digest();
}

